jQuery.fn.shiftChecker=function(){var e={};var b=0;var d=0;var a=null;var c=null;this.each(function(){if($(this).attr("type")=="checkbox"){var f=d;e[d]=$(this);$(this).click(function(g){if(g.shiftKey){a=f>b?b:f;c=f>b?f:b;while(a<=c){e[a++].attr("checked",true)}}b=f});d++}})};